export default{
    getUsers: ()=>{
        return fetch('http://localhost:5000/user',
                    {mode:'no-cors'})
        .then(res=> res.json())
        .then(data=> data);
    },
    deleteUser: (_id)=>{
        return fetch(`http://localhost:5000/user/${_id}`,
                    {method : 'delete',
                     mode: 'no-cors'})
                    .then(res => res.json())
                    .then(data => data);
    },
    updateUser: (user)=>{
        return fetch(`http://localhost:5000/user/${user._id}`,
                    {method: 'put',
                    mode: 'no-cors',
                     body: JSON.stringify(user),
                    headers:{
                        "Content-Type": "application/json"
                    }}).then(res => res.json())
                       .then(data => data);
    },
    createUser: (user)=>{
        return fetch(`http://localhost:5000/user`,
                    {method: 'post',
                    mode: 'no-cors',
                     body: JSON.stringify(user),
                    headers:{
                        "Content-Type": "application/json"
                    }}).then(res => res.json())
                       .then(data => data);
    }
}
